import React from "react";
// import { useState } from "react";
import addition from "./addition";
import subtraction from "./subtraction";
import multiplication from "./multiplication";
import division from "./division";

const Calculator=()=> {
  // const addition = () => {};
  // const subtraction = () => {};
  // const multiplication = () => {};
  // const division = () => {};

  // const [value, setvalue]=useState("value");
  const [input, setInput] = useState('');
  const [result, setResult] = useState('');

  const handleClick = (value) => {
    setInput((prev) => prev + value);
  };

  const handleClear = () => {
    setInput('');
    setResult('');
  };

  const handleBackspace = () => {
    setInput((prev) => prev.slice(0, -1));
  };

  const handleCalculate = () => {
    try {
      setResult(eval(input)); // Note: Using eval can be dangerous. Avoid in real-world applications.
    } catch (error) {
      setResult('Error');
    }
  };
  return (
    <div className=" 2xl:container border-4 text-3xl m-4  dark:bg-gray-900">
      <addition></addition>
      <subtraction></subtraction>
      <multiplication></multiplication>
      <division></division>

      <div >
        <input
          type="text"
          id="default-input"
          class="bg-gray-50 m-2 border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-60 p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
        ></input>
        <div className="display-flex px-4 py-2 h-50% w-80% text-20px border-radius-5">
          <button onClick={handleClear} class="relative inline-flex items-center justify-center p-0.5 mb-2 me-2 overflow-hidden text-sm font-medium text-gray-900 rounded-lg group bg-gradient-to-br from-cyan-500 to-blue-500 group-hover:from-cyan-500 group-hover:to-blue-500 hover:text-white dark:text-white focus:ring-4 focus:outline-none focus:ring-cyan-200 dark:focus:ring-cyan-800">
            <span class="relative px-5 py-2.5 transition-all ease-in duration-75 bg-white dark:bg-gray-900 rounded-md group-hover:bg-opacity-0">
              AC
